/**
 * Developer Dashboard Shortcode and Functionality
 * Add this to Code Snippets plugin
 * Use shortcode [developer_dashboard] on your Developer Dashboard page
 */

// Developer Dashboard Shortcode
add_shortcode('developer_dashboard', 'jobmatch_developer_dashboard');

function jobmatch_developer_dashboard($atts) {
    // Security check
    if (!is_user_logged_in()) {
        return '<p>Please <a href="' . wp_login_url() . '">log in</a> to access your dashboard.</p>';
    }

    $current_user = wp_get_current_user();
    $user_role = get_user_meta($current_user->ID, 'user_role', true);

    if ($user_role !== 'developer') {
        return '<p>Access denied. This dashboard is for developers only.</p>';
    }

    // Check if onboarding is complete
    $onboarding_complete = get_user_meta($current_user->ID, 'onboarding_complete', true);
    if (!$onboarding_complete) {
        wp_redirect(home_url('/onboarding/'));
        exit;
    }

    $atts = shortcode_atts(array(
        'view' => 'overview'
    ), $atts);

    // Get current view from URL parameter
    $current_view = $_GET['view'] ?? $atts['view'];

    ob_start();
    ?>

    <div class="developer-dashboard">
        <!-- Dashboard Header -->
        <div class="dashboard-header">
            <div class="user-welcome">
                <?php echo jobmatch_get_display_avatar($current_user->ID, null, 'large'); ?>
                <div class="welcome-text">
                    <h1>Welcome back, <?php echo jobmatch_get_display_name($current_user->ID); ?>!</h1>
                    <p><?php echo esc_html(get_user_meta($current_user->ID, 'profile_summary', true) ?: 'Complete your profile to get better matches'); ?></p>
                </div>
            </div>

            <div class="quick-stats">
                <?php echo jobmatch_render_developer_stats($current_user->ID); ?>
            </div>
        </div>

        <!-- Dashboard Navigation -->
        <nav class="dashboard-nav">
            <a href="?view=overview" class="nav-item <?php echo $current_view === 'overview' ? 'active' : ''; ?>">
                📊 Overview
            </a>
            <a href="?view=browse-jobs" class="nav-item <?php echo $current_view === 'browse-jobs' ? 'active' : ''; ?>">
                💼 Browse Jobs
            </a>
            <a href="?view=matches" class="nav-item <?php echo $current_view === 'matches' ? 'active' : ''; ?>">
                ❤️ My Matches
            </a>
            <a href="?view=messages" class="nav-item <?php echo $current_view === 'messages' ? 'active' : ''; ?>">
                💬 Messages
                <?php 
                $unread_count = jobmatch_get_unread_messages_count($current_user->ID);
                if ($unread_count > 0) echo '<span class="badge">' . $unread_count . '</span>';
                ?>
            </a>
            <a href="?view=profile" class="nav-item <?php echo $current_view === 'profile' ? 'active' : ''; ?>">
                👤 My Profile
            </a>
        </nav>

        <!-- Dashboard Content -->
        <div class="dashboard-content">
            <?php
            switch ($current_view) {
                case 'overview':
                    echo jobmatch_render_developer_overview($current_user->ID);
                    break;
                case 'browse-jobs':
                    echo jobmatch_render_job_browser($current_user->ID);
                    break;
                case 'matches':
                    echo jobmatch_render_matches($current_user->ID);
                    break;
                case 'messages':
                    echo jobmatch_render_messages($current_user->ID);
                    break;
                case 'profile':
                    echo jobmatch_render_profile_editor($current_user->ID);
                    break;
                default:
                    echo jobmatch_render_developer_overview($current_user->ID);
            }
            ?>
        </div>
    </div>

    <?php
    return ob_get_clean();
}

// Render developer statistics
function jobmatch_render_developer_stats($user_id) {
    $stats = jobmatch_get_match_stats($user_id);
    $profile_views = get_user_meta($user_id, 'profile_views_count', true) ?: 0;
    $last_active = get_user_meta($user_id, 'last_login', true);

    ob_start();
    ?>
    <div class="stats-grid">
        <div class="stat-card">
            <div class="stat-number"><?php echo $stats['total']; ?></div>
            <div class="stat-label">Total Interactions</div>
        </div>
        <div class="stat-card">
            <div class="stat-number"><?php echo $stats['mutual']; ?></div>
            <div class="stat-label">Mutual Matches</div>
        </div>
        <div class="stat-card">
            <div class="stat-number"><?php echo $stats['chatting']; ?></div>
            <div class="stat-label">Active Chats</div>
        </div>
        <div class="stat-card">
            <div class="stat-number"><?php echo $profile_views; ?></div>
            <div class="stat-label">Profile Views</div>
        </div>
    </div>
    <?php
    return ob_get_clean();
}

// Render developer overview
function jobmatch_render_developer_overview($user_id) {
    $recent_matches = jobmatch_get_user_matches($user_id, 'all', 5);
    $top_jobs = jobmatch_get_top_matches($user_id, 'job', 5);
    $notifications = jobmatch_get_user_notifications($user_id, 5);

    ob_start();
    ?>
