        <!-- Recent Activity -->
        <div class="overview-section">
            <h3>🔥 Recent Activity</h3>
            <?php if (!empty($recent_matches)): ?>
                <div class="activity-list">
                    <?php foreach ($recent_matches as $match): ?>
                        <?php
                        $other_user_id = $match->developer_id == $user_id ? $match->company_id : $match->developer_id;
                        $other_name = jobmatch_get_display_name($other_user_id, $user_id);
                        $time_ago = human_time_diff(strtotime($match->post_date));
                        ?>
                        <div class="activity-item">
                            <?php echo jobmatch_get_display_avatar($other_user_id, $user_id, 'small'); ?>
                            <div class="activity-text">
                                <strong><?php echo esc_html($other_name); ?></strong>
                                <span class="activity-action">
                                    <?php
                                    switch ($match->match_status) {
                                        case 'company_liked':
                                            echo 'is interested in you';
                                            break;
                                        case 'mutual':
                                            echo 'matched with you';
                                            break;
                                        case 'chatting':
                                            echo 'started chatting';
                                            break;
                                        default:
                                            echo 'interacted with you';
                                    }
                                    ?>
                                </span>
                                <span class="activity-time"><?php echo $time_ago; ?> ago</span>
                            </div>
                        </div>
                    <?php endforeach; ?>
                </div>
            <?php else: ?>
                <p class="empty-state">No recent activity. Start browsing jobs to get matches!</p>
            <?php endif; ?>
        </div>

        <!-- Top Job Matches -->
        <div class="overview-section">
            <h3>🎯 Top Job Matches</h3>
            <?php if (!empty($top_jobs)): ?>
                <div class="top-matches">
                    <?php foreach ($top_jobs as $job_match): ?>
                        <?php
                        $job = get_post($job_match->target_id);
                        if ($job) {
                            $company_id = $job->post_author;
                            $company_profile = jobmatch_get_anonymous_profile($company_id, $user_id);
                        ?>
                        <div class="match-preview">
                            <div class="match-header">
                                <?php echo jobmatch_get_display_avatar($company_id, $user_id, 'small'); ?>
                                <div class="match-info">
                                    <h4><?php echo esc_html($job->post_title); ?></h4>
                                    <p><?php echo esc_html($company_profile['name']); ?></p>
                                </div>
                                <div class="match-score excellent"><?php echo $job_match->score; ?>%</div>
                            </div>
                            <div class="match-actions">
                                <a href="<?php echo get_permalink($job->ID); ?>" class="btn-view">View Job</a>
                                <button class="btn-like" onclick="likeJob(<?php echo $job->ID; ?>, <?php echo $company_id; ?>)">❤️ Like</button>
                            </div>
                        </div>
                        <?php } ?>
                    <?php endforeach; ?>
                </div>
            <?php else: ?>
                <p class="empty-state">Complete your profile to see personalized job recommendations.</p>
            <?php endif; ?>
        </div>

        <!-- Notifications -->
        <div class="overview-section">
            <h3>🔔 Recent Notifications</h3>
            <?php if (!empty($notifications)): ?>
                <div class="notifications-list">
                    <?php foreach ($notifications as $notification): ?>
                        <div class="notification-item <?php echo $notification->is_read ? 'read' : 'unread'; ?>">
                            <div class="notification-icon">
                                <?php
                                $icons = array(
                                    'new_match' => '💙',
                                    'mutual_match' => '🎉',
                                    'high_match_job' => '🎯',
                                    'message' => '💬',
                                    'profile_view' => '👀'
                                );
                                echo $icons[$notification->type] ?? '📢';
                                ?>
                            </div>
                            <div class="notification-content">
                                <h4><?php echo esc_html($notification->title); ?></h4>
                                <p><?php echo esc_html($notification->message); ?></p>
                                <span class="notification-time"><?php echo human_time_diff(strtotime($notification->created_at)); ?> ago</span>
                            </div>
                            <?php if (!$notification->is_read): ?>
                                <button class="mark-read" onclick="markNotificationRead(<?php echo $notification->id; ?>)">✓</button>
                            <?php endif; ?>
                        </div>
                    <?php endforeach; ?>
                </div>
                <a href="?view=notifications" class="view-all">View All Notifications</a>
            <?php else: ?>
                <p class="empty-state">No notifications yet.</p>
            <?php endif; ?>
        </div>

        <!-- Profile Completion -->
        <div class="overview-section">
            <h3>📝 Profile Strength</h3>
            <?php echo jobmatch_render_profile_completion($user_id); ?>
        </div>
    </div>
    <?php
