// Render filtered jobs
function jobmatch_render_filtered_jobs($user_id, $filters) {
    $args = array(
        'post_type' => 'jobs',
        'post_status' => 'publish',
        'posts_per_page' => 20,
        'meta_query' => array(),
        'tax_query' => array()
    );

    // Apply filters
    if ($filters['tech_stack']) {
        $args['tax_query'][] = array(
            'taxonomy' => 'tech_stack',
            'field' => 'slug',
            'terms' => $filters['tech_stack']
        );
    }

    if ($filters['salary_min']) {
        $args['meta_query'][] = array(
            'key' => 'salary_min',
            'value' => intval($filters['salary_min']),
            'compare' => '>='
        );
    }

    if ($filters['work_type']) {
        $args['meta_query'][] = array(
            'key' => 'work_type',
            'value' => $filters['work_type'],
            'compare' => '='
        );
    }

    if ($filters['location'] && $filters['location'] !== 'remote') {
        $args['meta_query'][] = array(
            'key' => 'job_location',
            'value' => $filters['location'],
            'compare' => 'LIKE'
        );
    }

    $jobs = get_posts($args);

    if (empty($jobs)) {
        return '<div class="no-results">No jobs found matching your criteria. Try adjusting your filters.</div>';
    }

    ob_start();
    ?>
    <div class="jobs-grid">
        <?php foreach ($jobs as $job): ?>
            <?php
            $company_id = $job->post_author;
            $company_profile = jobmatch_get_anonymous_profile($company_id, $user_id);
            $match_score = jobmatch_get_match_score($user_id, $job->ID, 'job');
            $salary_min = get_post_meta($job->ID, 'salary_min', true);
            $salary_max = get_post_meta($job->ID, 'salary_max', true);
            $work_type = get_post_meta($job->ID, 'work_type', true);
            $job_location = get_post_meta($job->ID, 'job_location', true);
            $remote_work = get_post_meta($job->ID, 'remote_work', true);
            $tech_skills = wp_get_post_terms($job->ID, 'tech_stack', array('fields' => 'names'));
            ?>

            <div class="job-card" data-job-id="<?php echo $job->ID; ?>">
                <div class="job-header">
                    <?php echo jobmatch_get_display_avatar($company_id, $user_id, 'medium'); ?>
                    <div class="job-info">
                        <h3><?php echo esc_html($job->post_title); ?></h3>
                        <p class="company-name"><?php echo esc_html($company_profile['name']); ?></p>
                        <p class="job-location">
                            📍 <?php echo $remote_work ? 'Remote' : esc_html($job_location); ?>
                            <?php if ($work_type): ?>
                                • <?php echo ucfirst($work_type); ?>
                            <?php endif; ?>
                        </p>
                    </div>
                    <?php if ($match_score): ?>
                        <div class="match-score <?php echo jobmatch_get_score_class($match_score->score); ?>">
                            <?php echo $match_score->score; ?>%
                        </div>
                    <?php endif; ?>
                </div>

                <div class="job-details">
                    <?php if ($salary_min || $salary_max): ?>
                        <p class="salary">
                            💰 €<?php echo number_format($salary_min); ?>
                            <?php if ($salary_max && $salary_max !== $salary_min): ?>
                                - €<?php echo number_format($salary_max); ?>
                            <?php endif; ?>
                        </p>
                    <?php endif; ?>

                    <?php if (!empty($tech_skills)): ?>
                        <div class="tech-skills">
                            <?php foreach (array_slice($tech_skills, 0, 4) as $skill): ?>
                                <span class="tech-tag"><?php echo esc_html($skill); ?></span>
                            <?php endforeach; ?>
                            <?php if (count($tech_skills) > 4): ?>
                                <span class="tech-tag more">+<?php echo count($tech_skills) - 4; ?> more</span>
                            <?php endif; ?>
                        </div>
                    <?php endif; ?>

                    <div class="job-excerpt">
                        <?php echo wp_trim_words($job->post_content, 20); ?>
                    </div>
                </div>

                <div class="job-actions">
                    <button class="btn-like" onclick="likeJob(<?php echo $job->ID; ?>, <?php echo $company_id; ?>)">
                        ❤️ Like
                    </button>
                    <button class="btn-pass" onclick="passJob(<?php echo $job->ID; ?>, <?php echo $company_id; ?>)">
                        👋 Pass
                    </button>
                    <a href="<?php echo get_permalink($job->ID); ?>" class="btn-view">👁️ View Details</a>
                </div>

                <div class="job-meta">
                    <span class="posted-date">Posted <?php echo human_time_diff(strtotime($job->post_date)); ?> ago</span>
                </div>
            </div>
        <?php endforeach; ?>
    </div>

    <script>
    function likeJob(jobId, companyId) {
        jQuery.post(ajaxurl, {
            action: 'handle_match_action',
            target_user_id: companyId,
            action: 'like',
            job_id: jobId,
            nonce: jobmatch_nonce
        }, function(response) {
            if (response.success) {
                const button = document.querySelector(`[data-job-id="${jobId}"] .btn-like`);
                if (response.data.status === 'mutual_match') {
                    showMatchModal(response.data);
                    button.textContent = '💬 Chat Now';
                    button.onclick = () => openChat(response.data.match_id);
                } else {
                    button.textContent = '⏳ Liked';
                    button.disabled = true;
                }
            } else {
                alert('Error: ' + response.data);
            }
        });
    }

    function passJob(jobId, companyId) {
        if (confirm('Are you sure you want to pass on this job?')) {
            jQuery.post(ajaxurl, {
                action: 'handle_match_action',
                target_user_id: companyId,
                action: 'pass',
                job_id: jobId,
                nonce: jobmatch_nonce
            }, function(response) {
                if (response.success) {
                    const jobCard = document.querySelector(`[data-job-id="${jobId}"]`);
                    jobCard.style.opacity = '0.5';
                    jobCard.querySelector('.btn-pass').textContent = '✓ Passed';
                }
            });
        }
    }
    </script>
    <?php
    return ob_get_clean();
}

// Helper function to get score class
function jobmatch_get_score_class($score) {
    if ($score >= 90) return 'excellent';
    if ($score >= 80) return 'very-good';
    if ($score >= 70) return 'good';
    if ($score >= 60) return 'fair';
    return 'poor';
}

