// Calculate profile completion
function jobmatch_calculate_profile_completion($user_id) {
    $required_fields = array(
        'tech_skills' => 'Add your technical skills',
        'salary_expectation' => 'Set salary expectation',
        'work_preference' => 'Choose work preference',
        'experience_level' => 'Set experience level',
        'postal_code' => 'Add your location',
        'profile_summary' => 'Write a profile summary',
        'linkedin_profile' => 'Add LinkedIn profile',
        'portfolio_url' => 'Add portfolio URL'
    );

    $completed = 0;
    $missing = array();

    foreach ($required_fields as $field => $description) {
        $value = get_user_meta($user_id, $field, true);
        if ($value && !empty($value)) {
            $completed++;
        } else {
            $missing[] = $description;
        }
    }

    $percentage = round(($completed / count($required_fields)) * 100);

    $strength = 'Getting Started';
    $message = 'Complete your profile to attract better opportunities.';

    if ($percentage >= 90) {
        $strength = 'Excellent';
        $message = 'Your profile is complete and attractive to employers!';
    } elseif ($percentage >= 70) {
        $strength = 'Good';
        $message = 'Your profile looks good. A few more details would help.';
    } elseif ($percentage >= 50) {
        $strength = 'Fair';
        $message = 'You\'re halfway there! Keep building your profile.';
    }

    return array(
        'percentage' => $percentage,
        'strength' => $strength,
        'message' => $message,
        'missing' => $missing
    );
}

