// Enqueue dashboard scripts and styles
add_action('wp_enqueue_scripts', 'jobmatch_enqueue_dashboard_assets');

function jobmatch_enqueue_dashboard_assets() {
    if (is_page() && has_shortcode(get_post()->post_content, 'developer_dashboard')) {
        wp_enqueue_script('jquery');

        // Add nonce for AJAX calls
        wp_localize_script('jquery', 'jobmatch_ajax', array(
            'ajax_url' => admin_url('admin-ajax.php'),
            'nonce' => wp_create_nonce('jobmatch_nonce')
        ));

        // Add inline styles
        wp_add_inline_style('wp-block-library', jobmatch_get_dashboard_styles());
    }
}

