    }

    .stat-label {
        font-size: 0.8rem;
        opacity: 0.9;
    }

    .dashboard-nav {
        display: flex;
        gap: 0;
        background: white;
        border-radius: 15px;
        padding: 5px;
        box-shadow: 0 4px 15px rgba(0,0,0,0.1);
        margin-bottom: 30px;
    }

    .nav-item {
        flex: 1;
        padding: 15px 20px;
        text-decoration: none;
        color: #666;
        border-radius: 10px;
        text-align: center;
        font-weight: bold;
        transition: all 0.3s ease;
        position: relative;
    }

    .nav-item.active {
        background: linear-gradient(135deg, #667eea, #764ba2);
        color: white;
    }

    .nav-item .badge {
        background: #dc3545;
        color: white;
        border-radius: 10px;
        padding: 2px 6px;
        font-size: 0.7rem;
        position: absolute;
        top: 5px;
        right: 10px;
    }

    .overview-grid {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
        gap: 25px;
    }

    .overview-section {
        background: white;
        border-radius: 15px;
        padding: 25px;
        box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    }

    .overview-section h3 {
        margin: 0 0 20px 0;
        color: #333;
        font-size: 1.2rem;
    }

    .activity-list {
        display: flex;
        flex-direction: column;
        gap: 15px;
    }

    .activity-item {
        display: flex;
        align-items: center;
        gap: 12px;
        padding: 12px;
        background: #f8f9fa;
        border-radius: 10px;
    }

    .activity-text {
        display: flex;
        flex-direction: column;
        gap: 2px;
    }

    .activity-action {
        color: #666;
        font-size: 0.9rem;
    }

    .activity-time {
        color: #999;
        font-size: 0.8rem;
    }

    .job-filters {
        background: white;
        padding: 25px;
        border-radius: 15px;
        margin-bottom: 25px;
        box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    }

    .filter-row {
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
        gap: 15px;
        align-items: end;
    }

    .filter-group label {
        display: block;
        font-weight: bold;
        margin-bottom: 5px;
        color: #333;
    }

    .filter-group select,
    .filter-group input {
        width: 100%;
        padding: 10px;
        border: 2px solid #e9ecef;
        border-radius: 8px;
        font-size: 0.9rem;
    }

    .btn-filter,
    .btn-clear {
        padding: 10px 20px;
        border: none;
        border-radius: 8px;
        cursor: pointer;
        font-weight: bold;
        text-decoration: none;
        display: inline-block;
        text-align: center;
    }

    .btn-filter {
        background: linear-gradient(135deg, #667eea, #764ba2);
        color: white;
    }

    .btn-clear {
        background: #6c757d;
        color: white;
    }

    .jobs-grid {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(400px, 1fr));
        gap: 25px;
    }

    .job-card {
        background: white;
        border-radius: 15px;
        padding: 25px;
        box-shadow: 0 4px 15px rgba(0,0,0,0.1);
        transition: transform 0.3s ease;
    }

    .job-card:hover {
        transform: translateY(-5px);
    }

    .job-header {
        display: flex;
        align-items: flex-start;
        gap: 15px;
        margin-bottom: 15px;
    }

    .job-info h3 {
        margin: 0;
        color: #333;
        font-size: 1.2rem;
    }

    .company-name {
        color: #666;
        margin: 5px 0;
    }

    .job-location {
        color: #999;
        font-size: 0.9rem;
        margin: 0;
    }

    .tech-skills {
        display: flex;
        flex-wrap: wrap;
        gap: 8px;
        margin: 15px 0;
    }

    .tech-tag {
        background: #e9ecef;
        padding: 4px 10px;
        border-radius: 12px;
        font-size: 0.8rem;
        color: #495057;
        font-weight: 500;
    }

    .tech-tag.more {
        background: #007cba;
        color: white;
    }

    .job-actions {
        display: flex;
        gap: 10px;
        margin-top: 15px;
    }

    .job-actions button,
    .job-actions a {
        padding: 8px 16px;
        border: none;
        border-radius: 20px;
        cursor: pointer;
        text-decoration: none;
        font-weight: bold;
        font-size: 0.9rem;
        transition: all 0.3s ease;
    }

    .btn-like {
        background: linear-gradient(135deg, #fa709a, #fee140);
        color: white;
    }

    .btn-pass {
        background: #6c757d;
        color: white;
    }

    .btn-view {
        background: #f8f9fa;
        color: #333;
        border: 2px solid #dee2e6;
    }

    .job-meta {
        margin-top: 15px;
        padding-top: 15px;
        border-top: 1px solid #e9ecef;
        color: #999;
        font-size: 0.8rem;
    }

    .empty-state {
        text-align: center;
        padding: 40px;
        color: #666;
        font-style: italic;
    }

    .no-results {
        text-align: center;
        padding: 60px 20px;
        color: #666;
        background: white;
        border-radius: 15px;
        box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    }

    @media (max-width: 768px) {
        .dashboard-header {
            flex-direction: column;
            gap: 20px;
            text-align: center;
        }

        .stats-grid {
            grid-template-columns: repeat(2, 1fr);
        }

        .overview-grid {
            grid-template-columns: 1fr;
        }

        .jobs-grid {
            grid-template-columns: 1fr;
        }

        .filter-row {
            grid-template-columns: 1fr;
        }
    }
    ';
}
