/**
 * Missing Connections Fix
 * Fixes missing connections and hooks for JobMatch platform
 */

// Fix geo-location profile hook
if (function_exists('jobmatch_geocode_user_location')) {
    add_action('profile_update', 'jobmatch_geocode_user_location');
    add_action('user_register', 'jobmatch_geocode_user_location');
}

// Add missing jobmatch_filters shortcode
if (!shortcode_exists('jobmatch_filters')) {
    add_shortcode('jobmatch_filters', function() {
        // Try to use existing filter functions
        if (function_exists('jobmatch_render_filter_ui')) {
            return jobmatch_render_filter_ui();
        } elseif (function_exists('jobmatch_render_advanced_filters')) {
            return jobmatch_render_advanced_filters();
        }
        return '<p>Filters not configured</p>';
    });
}

// Subscription placeholder until Stripe is ready
if (!shortcode_exists('jobmatch_subscription_manager')) {
    add_shortcode('jobmatch_subscription_manager', function() {
        return '<div style="padding: 20px; background: #f5f5f5; border-radius: 8px;">
            <h3>Subscription Plans</h3>
            <p>Monthly subscription plans will be available soon.</p>
        </div>';
    });
}

// Connect missing AJAX endpoints
if (!has_action('wp_ajax_jobmatch_browse_with_filters')) {
    add_action('wp_ajax_jobmatch_browse_with_filters', function() {
        if (function_exists('jobmatch_handle_filtered_browse')) {
            jobmatch_handle_filtered_browse();
        } else {
            wp_send_json_error('Filter handler not found');
        }
    });
    add_action('wp_ajax_nopriv_jobmatch_browse_with_filters', function() {
        if (function_exists('jobmatch_handle_filtered_browse')) {
            jobmatch_handle_filtered_browse();
        } else {
            wp_send_json_error('Please login to use filters');
        }
    });
}
